"import {\n    MU_SUN,\n    AU_EARTH,\n    MU_EARTH\n} from './planets';\n//time\nexport const SECOND: number = 1\nexport const MINUTE: number = SECOND * 60;\nexport const HOUR: number = MINUTE * 60;\nexport const DAY: number = HOUR * 24;\nexport const YEAR: number = DAY * 365;\nexport const MONTH: number = YEAR / 12;\n\n//scale\nexport const TIME_SCALE: number = MONTH;\nexport const T_MERCURY: number = 88 * DAY;\n\n//steps\nexport const STEPS_PER_ORBIT: number = 1000;\nexport const DT: number = MINUTE; // e.g. 60 s per step\n\n//length\nexport const MAX_TRAIL_LENGTH: number = 2000;\n\n//surface softening\nexport const SOFTENING: number = 1e8; // Reduced for surface launches\n\n\nexport const toMeters = (v: number, unit: string): number => {\n    switch (unit) {\n        case 'km': return v * 1000;\n        case 'mi': return v * 1609.34;\n        case 'ft': return v * 0.3048;\n        default: return v;\n    }\n};\n\nexport const toSeconds = (v: number, unit: string): number => {\n    switch (unit) {\n        case 'min': return v * 60;\n        case 'h': return v * 3600;\n        case 'day': return v * 86400;\n        default: return v;\n    }\n};\n\nexport const fromMeters = (d: number, unit: string): number => {\n    switch (unit) {\n        case 'km': return d / 1000;\n        case 'mi': return d / 1609.34;\n        case 'ft': return d / 0.3048;\n        default: return d;\n    }\n};\nexport const velocityToMs = (value: number, unit: string): number => {\n    switch (unit) {\n        case 'km/s': return value * 1000;\n        case 'mph': return value * 0.44704;  // 1 mph = 0.44704 m/s\n        case 'ft/s': return value * 0.3048;  // 1 ft/s = 0.3048 m/s\n        default: return value;  // m/s\n    }\n};\n\nexport const velocityFromMs = (value: number, unit: string): number => {\n    switch (unit) {\n        case 'km/s': return value / 1000;\n        case 'mph': return value / 0.44704;\n        case 'ft/s': return value / 0.3048;\n        default: return value;\n    }\n};\nexport const fromMps = (v: number, dist_unit: string, time_unit: string): number => {\n    const dist_conv: number = toMeters(1, dist_unit);\n    const time_conv: number = toSeconds(1, time_unit);\n    return v * (time_conv / dist_conv);\n};\n\n\n/**\n * Convert meters to pixels, based on Earthâ€™s semi-major axis as 1 AU.\n * @param meters - distance in meters\n * @returns equivalent length in pixels\n */\nexport function toPixels(meters: number): number {\n    return (meters / AU_EARTH) * PIXELS_PER_AU;\n}\n\n/**\n * Convert gravitational parameter (mÂ³/sÂ²) to pxÂ³/sÂ².\n * @param mu - gravitational parameter in mÂ³/sÂ²\n * @returns equivalent in pxÂ³/sÂ²\n */\nexport function mu_m3(mu: number): number {\n    return mu * Math.pow(PIXELS_PER_METER, 3);\n}\n// Unit conversion constants\nexport const PIXELS_PER_AU: number = 130;                // px in 1 AU\nexport const METERS_PER_AU: number = AU_EARTH;\nexport const PIXELS_PER_METER: number = PIXELS_PER_AU / METERS_PER_AU;\nexport const MU_PER_PX: number = mu_m3(MU_SUN);\n\n\n\nexport const getObjUnits = (\n    sm_axis: number,\n    eccentricity: number\n) => {\n    const p: number = sm_axis * (1 - eccentricity ** 2);\n    const T: number = 2 * Math.PI * Math.sqrt(sm_axis ** 3 / MU_EARTH);\n    const n: number = 2 * Math.PI / T;\n    const apo: number = sm_axis * (1 + eccentricity);\n    return { p, T, n, apo };\n};\n"


