
def chksum16(data: list) -> int:
    src = 0
    size = len(data)

    acc = 0
    # /* data may be at odd or even addresses */
    octetptr = 0
    while size > 1:
        # /* declare first octet as most significant
        # thus assume network order, ignoring host order */
        src = data[octetptr] << 8
        octetptr += 1
        # /* declare second octet as least significant */
        src |= data[octetptr]
        octetptr += 1
        acc += src
        size -= 2

    if size > 0:
        # /* accumulate remaining octet */
        src = data[octetptr] << 8
        acc += src

    # /* add deferred carry bits */
    acc = (acc >> 16) + (acc & 0x0000ffff)
    if (acc & 0xffff0000) != 0:
        acc = (acc >> 16) + (acc & 0x0000ffff)

    return acc


