dumpxh(ap.flip(gyou, 3, 3, 'y'), 3)
print("[ Flip XY (3x3) ]")
print("ap.flip(gyou, 3, 3, \x22z\x22)")
dumpxh(ap.flip(gyou, 3, 3, 'z'), 3)
