void multi_random_msg_correctness(void) {

  unsigned int i, j;
  int ret;
  uint8_t m[MLEN] = {0};
  uint8_t sm[SIG_BYTES + MLEN];
  uint8_t m2[SIG_BYTES + MLEN];
  uint8_t pk[SIG_PUBLICKEYBYTES];
  uint8_t sk[SIG_SECRETKEYBYTES];
  size_t sm_byts, mlen;
  uint8_t ctx[MLEN];
  randombytes(ctx, MLEN);

  for(i = 0; i < NTESTS; ++i) {
    randombytes(m, MLEN);

    ret = crypto_sign_keypair(pk, sk);
    if(ret != 0) {
      printf("[ERROR] Error occured when doing crypto_sign_keypair.\n");
      return;
    }
#ifdef SHOW_MSG
    if(i == NTESTS/2 + 198) {
      puts("pk:");
      for(int i = 0; i < SIG_PUBLICKEYBYTES; i++) {
        printf("0x%x ", pk[i]);
      }
      printf("\n");
      puts("sk:");
      for(int i = 0; i < SIG_SECRETKEYBYTES; i++) {
        printf("0x%x ", sk[i]);
      }
      printf("\n");
    }
#endif

    // ret = crypto_sign_signature(sm, &sm_byts, m, MLEN, sk);
    ret = crypto_sign(sm, &sm_byts, m, MLEN, ctx, MLEN, sk);
    if(ret < 0) {
      printf("[ERROR] Error occured when doing crypto_sign_signature.\n");
      return;
    }
    if(sm_byts != SIG_BYTES + MLEN) {
      printf("[ERROR] Sig bytes error.\n");
      return;
    }
#ifdef SHOW_MSG
    if(i == NTESTS/2 + 198) {
      puts("m:");
      for(int i = 0; i < MLEN; i++) {
        printf("0x%x ", m[i]);
      }
      printf("\n");
      puts("sm:");
      for(int i = 0; i < sm_byts; i++) {
        printf("0x%x ", sm[i]);
      }
      printf("\n");
    }
#endif

    // ret = crypto_sign_verify(sm, sm_byts, m, MLEN, pk);
    ret = crypto_sign_open(m2, &mlen, sm, sm_byts, ctx, MLEN, pk);
    if(ret) {
      fprintf(stderr, "[ERROR] Verification failed at round %d\n", i);
      return;
    }

    if(mlen != MLEN) {
      fprintf(stderr, "[ERROR] Message lengths don't match\n");
      return;
    }

    for(j = 0; j < mlen; ++j) {
      if(m[j] != m2[j]) {
        fprintf(stderr, "[ERROR] Messages don't match\n");
        return;
      }
    }

    randombytes((uint8_t *)&j, sizeof(j));
    uint8_t tmp_sm[CRYPTO_BYTES + MLEN] = {0};
    memcpy(tmp_sm, sm, CRYPTO_BYTES + MLEN);
    do {
      randombytes(m2, 1);
    } while(!m2[0]);
    sm[j % CRYPTO_BYTES] += m2[0];
    ret = crypto_sign_open(m2, &mlen, sm, sm_byts, ctx, MLEN, pk);
    if(ret == 0) {
      fprintf(stderr, "[-] Trivial forgeries possible at round %d\n", i);
      return;
    }
  }

  printf("[+] Test success.\n");

  printf("SIG_PUBLICKEYBYTES = %d\n", SIG_PUBLICKEYBYTES);
  printf("SIG_SECRETKEYBYTES = %d\n", SIG_SECRETKEYBYTES);
  printf("SIG_BYTES = %d\n", SIG_BYTES);
  printf("================= Finish aigis sign/verify test ================\n");
}


int main(void)
{

  multi_random_msg_correctness();
//   single_msg_correctness();

  return 0;
}
